	/**
	 * This is the primary method that runs the entire pipeline and updates the outputs.
	 */
	public void process(Mat source0) {
		// Step CV_flip0:
		Mat cvFlipSrc = source0;
		FlipCode cvFlipFlipcode = FlipCode.BOTH_AXES;
		cvFlip(cvFlipSrc, cvFlipFlipcode, cvFlipOutput);

		// Step CV_cvtColor0:
		Mat cvCvtcolorSrc = cvFlipOutput;
		int cvCvtcolorCode = Imgproc.COLOR_RGB2YCrCb;
		cvCvtcolor(cvCvtcolorSrc, cvCvtcolorCode, cvCvtcolorOutput);

		// Step CV_extractChannel0:
		Mat cvExtractchannelSrc = cvCvtcolorOutput;
		double cvExtractchannelChannel = 1.0;
		cvExtractchannel(cvExtractchannelSrc, cvExtractchannelChannel, cvExtractchannelOutput);

		// Step CV_Threshold0:
		Mat cvThresholdSrc = cvExtractchannelOutput;
		double cvThresholdThresh = 125.0;
		double cvThresholdMaxval = 255.0;
		int cvThresholdType = Imgproc.THRESH_BINARY_INV;
		cvThreshold(cvThresholdSrc, cvThresholdThresh, cvThresholdMaxval, cvThresholdType, cvThresholdOutput);

		// Step Blur0:
		Mat blur0Input = cvThresholdOutput;
		BlurType blur0Type = BlurType.get("Median Filter");
		double blur0Radius = 31.775700934579437;
		blur(blur0Input, blur0Type, blur0Radius, blur0Output);

		// Step CV_erode0:
		Mat cvErodeSrc = blur0Output;
		Mat cvErodeKernel = new Mat();
		Point cvErodeAnchor = new Point(-1, -1);
		double cvErodeIterations = 11.0;
		int cvErodeBordertype = Core.BORDER_CONSTANT;
		Scalar cvErodeBordervalue = new Scalar(-1);
		cvErode(cvErodeSrc, cvErodeKernel, cvErodeAnchor, cvErodeIterations, cvErodeBordertype, cvErodeBordervalue, cvErodeOutput);

		// Step Blur1:
		Mat blur1Input = cvErodeOutput;
		BlurType blur1Type = BlurType.get("Median Filter");
		double blur1Radius = 24.29906542056074;
		blur(blur1Input, blur1Type, blur1Radius, blur1Output);

		// Step CV_dilate0:
		Mat cvDilateSrc = blur1Output;
		Mat cvDilateKernel = new Mat();
		Point cvDilateAnchor = new Point(-1, -1);
		double cvDilateIterations = 2.0;
		int cvDilateBordertype = Core.BORDER_CONSTANT;
		Scalar cvDilateBordervalue = new Scalar(-1);
		cvDilate(cvDilateSrc, cvDilateKernel, cvDilateAnchor, cvDilateIterations, cvDilateBordertype, cvDilateBordervalue, cvDilateOutput);

		// Step Find_Contours0:
		Mat findContoursInput = cvDilateOutput;
		boolean findContoursExternalOnly = true;
		findContours(findContoursInput, findContoursExternalOnly, findContoursOutput);

		// Step Convex_Hulls0:
		ArrayList<MatOfPoint> convexHullsContours = findContoursOutput;
		convexHulls(convexHullsContours, convexHullsOutput);

	}

